package com.symantec.vip.automation.mobile.screens;import java.lang.reflect.Field;import org.openqa.selenium.By;import io.appium.java_client.AppiumDriver;/** *  * Abstract class representation of a Page in the UI. Page object pattern */public abstract class Page {	public AppiumDriver driver;	public String os;	/*	 * 	 * Constructor injecting the WebDriver interface	 * 	 * 	 * 	 * @param webDriver	 */	public Page(AppiumDriver driver, String os) {		this.driver = driver;		this.os = os;	}	public String getTitle() {		return driver.getTitle();	}	public By getLocator(Class<?> className, String fieldName) throws Exception {		By out = null;		Field[] fields = className.getFields();		for (Field field : fields) {			if (field.getName().equalsIgnoreCase(fieldName)) {				out = (By) field.get(className);				System.out.println("Element Locator is " + out);				break;			}		}		return out;	}}