package com.symantec.vip.automation.utils;import java.util.HashMap;import java.util.Map;import java.util.Properties;import org.apache.log4j.Logger;import com.symantec.vip.automation.restapi.RestClient;public class CodeCoverageProcedure {	static RestClient ent = new RestClient();	static Logger logger = Logger.getLogger("VIP_EG_AUTOMATION");	public static void executeCodeCoverageScript(Map<String, String> config) {		Properties properties = AutomationProperties.getInstance().GetAutomationProperties();		String egHost = config.get("eg_server_host");		logger.info("EG Host is where code coverage coverage configurtion to check is "+egHost);		String codeCoverageServerWindows = properties.getProperty("hostname.ui.windows_cc").trim();		String codeCoverageServerLinux = properties.getProperty("hostname.ui.linux_cc").trim();		if (egHost.equals(codeCoverageServerWindows) || egHost.equals(codeCoverageServerLinux)) {			Map<String, String> testParamForThisTest = new HashMap<String, String>();			logger.info(("Executing scripts in code coverage server to get CC data files"));			testParamForThisTest.put("eg_server_host", egHost);			testParamForThisTest.put("validation_server_host", egHost);			ent.PerformRemoteAction("GenerateJavaCodeCoverageReport", testParamForThisTest);		}	}}